for run = 1 : size(huff_table_fw,1)
    for bc = 1 : size(huff_table_fw,2)
        if isempty(huff_table_fw{run,bc})
            continue
        end
        pos = pos + 1;
        huff_table_bw(pos,:) = [bi2de(huff_table_fw{run,bc}', 'left-msb'), run-1, bc-1];
        if length(huff_table_fw{run,bc}) < min_huff_length
            min_huff_length = length(huff_table_fw{run,bc});
        end
    end
end
min_huff_length = min_huff_length;